using System;
using Microsoft.Azure.Functions.Worker;
using Microsoft.Extensions.Logging;

namespace ApWizard.Functions
{
    public class UserEventProcessor
    {
        private readonly ILogger _logger;

        public UserEventProcessor(ILoggerFactory loggerFactory)
        {
            _logger = loggerFactory.CreateLogger<UserEventProcessor>();
        }

        [Function("ProcessUserEvent")]
        public void Run([ServiceBusTrigger("user-events", "subscription-name", Connection = "ServiceBusConnection")] string mySbMsg, FunctionContext context)
        {
            _logger.LogInformation($"C# ServiceBus topic trigger function processed message: {mySbMsg}");
            _logger.LogInformation($"Message ID: {context.BindingContext.BindingData["MessageId"]}");
        }
    }
}
