SELECT c.name AS Name, o.name AS [Sysname]
FROM sys.columns c
JOIN sys.objects o ON c.object_id = o.object_id;

SELECT *
FROM GLPD
WHERE PartNo =1;

SELECT GLCo, PartNo, "P2Desc"=Description
FROM GLPD
WHERE PartNo=2;

SELECT GLCo, PartNo, "P3Desc"=Description
FROM GLPD
WHERE PartNo=3;

SELECT *
FROM GLPI
WHERE PartNo =1;

SELECT JCCM.*, DDDTShared.Secure
FROM dbo.JCCM (nolock)
LEFT JOIN dbo.DDDTShared WITH (nolock) ON Datatype = 'bJob';

SELECT *
FROM dbo.PMCO
INNER JOIN api.vBusinessUnit bu on bu.CompanyId = PMCO.KeyID;

SELECT StartOfWeek AS [StartOfWeek], sum([NetAmount]) AS [NetAmount]
FROM [CashFlowAP]
GROUP BY CashFlowAP.StartOfWeek;

SELECT StartOfWeek AS [StartOfWeek], sum([AmountDue]) AS [AmountDue]
FROM [CashFlowAR]
GROUP BY CashFlowAR.StartOfWeek;

SELECT StartOfWeek, sum(PayableAmount)AS [PayableAmount], sum(ReceivableAmount)AS [ReceivableAmount]
FROM [CashFlowAP-AR-ByWeek]
GROUP BY StartOfWeek;

SELECT isnull(vDDCBc.ComboType,vDDCB.ComboType) AS ComboType, isnull(vDDCBc.Description,vDDCB.Description) AS Description
FROM dbo.vDDCBc
full OUTER JOIN dbo.vDDCB ON vDDCB.ComboType = vDDCBc.ComboType;

SELECT TOP 100 percent vDDDS.*
FROM dbo.vDDDS WITH (nolock)
ORDER BY vDDDS.Datatype, vDDDS.Qualifier, vDDDS.Instance;

SELECT vDDFormRelated.*, vDDFH.Title
FROM dbo.vDDFormRelated
INNER JOIN dbo.vDDFH ON vDDFH.Form = vDDFormRelated.RelatedForm;

SELECT [KeyID] AS [DocumentTypeDefaultId], [DocType] AS [DocumentTypeCode], [DocCategory] AS [DocumentCategory]
FROM [dbo].[PMDT]
WHERE [DocType] LIKE 'TEAM%';

SELECT Co, Equipment, LastReadingDate = Max(ReadingDate)
FROM dbo.EMSM WITH(nolock)
GROUP BY Co,Equipment;

SELECT 1 as EnterpriseSettingId, InputMask as SettingValue, 'ProjectCodeInputMask' as SettingName
FROM DDDTShared
WHERE Datatype = 'bProject';

SELECT gc.GlobalIdentity, gc.GlobalUserId, fc.*
FROM [api].[PMFirmContact] fc
JOIN [api].[vGlobalContact] gc on gc.FirmContactId = fc.ContactId;

SELECT bHRCM.*
FROM dbo.bHRCM
WHERE bHRCM.Type = 'U';

SELECT DISTINCT dbo.bHRTC.TrainCode, dbo.bHRCM.Description, dbo.bHRTC.HRCo
FROM dbo.bHRTC WITH (nolock)
INNER JOIN dbo.bHRCM WITH (nolock) ON dbo.bHRTC.HRCo = dbo.bHRCM.HRCo AND dbo.bHRTC.TrainCode = dbo.bHRCM.Code AND dbo.bHRTC.Type = dbo.bHRCM.Type;

SELECT bIMKV.ImportId, bIMKV.ImportTemplate
FROM dbo.bIMKV
GROUP BY bIMKV.ImportId, bIMKV.ImportTemplate;

SELECT bIMPR.ImportId
FROM dbo.bIMPR
GROUP BY bIMPR.ImportId;

SELECT bIMWM.ImportId
FROM dbo.bIMWM
GROUP BY bIMWM.ImportId;

SELECT JCCM.*
FROM dbo.JCCM
WHERE JCCM.ContractStatus > 0;

SELECT bJCJM.*
FROM dbo.bJCJM
WHERE (( (
    EXISTS (SELECT 1
FROM dbo.vfDataTypeSecurity(bJCJM.Job, bJCJM.JCCo, 'bJob'))
    )) OR (EXISTS (SELECT 1
FROM dbo.vfIsSystemUser()
WHERE IsSystemUser = 1) )
) AND bJCJM.PCVisibleInJC = 'Y';

SELECT JCJM.*
FROM dbo.JCJM
WHERE JCJM.JobStatus > 0;

SELECT JCCo, Contract, sum(ProjDollars) AS [Proj-Contract]
FROM dbo.JCIP
GROUP BY JCCo, Contract;

SELECT JCPPCostTypes.Co, JCPPCostTypes.Mth, JCPPCostTypes.BatchId
FROM dbo.JCPPCostTypes
GROUP BY JCPPCostTypes.Co, JCPPCostTypes.Mth, JCPPCostTypes.BatchId;
